SELECT movie.title, actor.name FROM movie INNER JOIN actor ON movie.director = actor.id WHERE title LIKE 'Star Wars%' ORDER BY title;
SELECT actor.name FROM movie INNER JOIN casting ON movie.id = casting.movieid INNER JOIN actor ON casting.actorid = actor.id WHERE movie.title = 'Jurassic Park' ORDER BY ord;
SELECT movie.title FROM actor INNER JOIN casting ON actor.id = casting.actorid INNER JOIN movie ON casting.movieid = movie.id WHERE name = 'George Clooney';
SELECT movie.title, actor.name FROM movie INNER JOIN casting ON movie.id = casting.movieid INNER JOIN actor ON casting.actorid = actor.id WHERE movie.yr = 1920 and casting.ord = 1 ORDER BY movie.title;
SELECT movie.title, a1.name, a2.name FROM movie INNER JOIN casting ON movie.id = casting.movieid INNER JOIN actor AS a1 ON casting.actorid = a1.id INNER JOIN actor AS a2 ON movie.director = a2.id WHERE movie.yr = 1920 and casting.ord = 1 ORDER BY movie.title;
SELECT m.title, COUNT(*) as cnt FROM casting INNER JOIN movie as m ON casting.movieid = m.id GROUP BY m.id ORDER BY cnt DESC LIMIT 5;
SELECT yr, count FROM (SELECT yr, COUNT(*) as count FROM (movie INNER JOIN casting ON movie.id = casting.movieid INNER JOIN actor ON casting.actorid = actor.id) WHERE actor.name = 'Rock Hudson' GROUP BY yr) AS M0 WHERE count > 1 ORDER BY count DESC, yr ASC;
SELECT name FROM (SELECT movieid FROM (casting INNER JOIN actor ON casting.actorid = actor.id) AS T0 WHERE name = 'Harrison Ford') AS M0 INNER JOIN casting ON M0.movieid = casting.movieid INNER JOIN actor ON casting.actorid = actor. id WHERE casting.ord = 1 and name != 'Harrison Ford' GROUP BY name ORDER BY name;
SELECT title FROM movie INNER JOIN casting ON movie.id = casting.movieid INNER JOIN actor AS director ON movie.director = director.id INNER JOIN actor ON casting.actorid = actor.id WHERE director.name = 'Woody Allen' and actor.name = 'Woody Allen' ORDER BY title;
SELECT title FROM movie INNER JOIN casting ON movie.id = casting.movieid INNER JOIN actor AS director ON movie.director = director.id INNER JOIN actor ON casting.actorid = actor.id WHERE director.name = 'Woody Allen' or actor.name = 'Woody Allen' GROUP BY title ORDER BY title;
SELECT DISTINCT title FROM movie JOIN casting ON casting.movieid = movie.id WHERE casting.movieid IN (SELECT movieid FROM casting JOIN actor ON casting.actorid = actor.id WHERE name = 'Alain Delon') AND casting.movieid IN (SELECT movieid FROM casting JOIN actor ON casting.actorid = actor.id WHERE name = 'Catherine Deneuve');
SELECT name FROM (SELECT name, COUNT(*) FROM movie INNER JOIN casting ON movie.id = casting.movieid INNER JOIN actor ON casting.actorid = actor.id WHERE title LIKE 'Star Wars%' GROUP BY name ORDER BY count DESC LIMIT 1) as M0;
